import React, { Component } from 'react';
import logo from './logo.svg';
import './App.css';

class App extends Component {
  constructor(){
    super();
    this.state = {
      value: '',
      data: [],
      repoDetails: []
    }
  }

  handleChange = (e) => {
    var val = e.target.value;
    this.setState({
      value: val
    })
  }

 handelrepo = repo => (e) => {
  const url = `https://api.github.com/repos/${repo}/commits`;
  fetch(url).then(res => res.json()).then(data => this.setState({
    repoDetails: data
  }))
 }


 handleSubmit = (e) => {
  e.preventDefault();
  fetch(`https://api.github.com/orgs/${this.state.value}/repos`).then(res => res.json()).then(d => this.setState({
    data: d
  }))
 }
  render() {
    return (
      <div className="App">
        <header className="App-header">
        <form onSubmit={this.handleSubmit}>
        <input type="text" onChange={this.handleChange} placeholder="Search Org" id="input"/>
        </form>         
        </header>
        <div className="main_list">

        <div>
        <h1>Repo List</h1>
        {
          this.state.data.map((val, i) => {
            return <h4 onClick={this.handelrepo(val.full_name)} name={val.full_name}>{val.full_name}</h4>
          })
        }
        </div>

        <div>
        <h1>Repo Details</h1>
        {
          this.state.repoDetails.map(val => {
            console.log(val)
            return <h3>{val.author.login}</h3>
          })
        }
        </div>


        </div>
      </div>
    );
  }
}

export default App;
